import React, { Component } from "react";
import { getComments } from "../Service";
export default class CommentBox extends Component<{
  sak_id?: number
}> {
  constructor(props) {
    super(props);

    this.state = {
      comments: []
    };
  }
  componentDidMount() {
    getComments(this.props.sak_id)
      .then(response => {
        console.log(response);
        this.setState({ comments: response.data });
        this.setState({ isLoaded: true });
      })
      .catch(error => {
        console.log(error);
        this.setState({ errorMsg: "Error retreiving data" });
      });
  }
  render() {
    const { comments } = this.state;
    return (
      <div>
        <div className="commentBox">
          {comments.map(comment => commentCard(comment))}
          <div className="commentList">Yeahhhh I am a CommentList.</div>
          <div className="commentForm">
            Party Parrot time. I am a CommentForm.
          </div>
        </div>
      </div>
    );
  }
}

function commentCard(comments) {
  return (
    <div className="comment p-2 border-5">
      <h4 className="commentAuthor">{comments.brukernavn}</h4>
      <hr />
      <div>{comments.kommentar}</div>
    </div>
  );
}
