import React, { Component } from "react";
import { getComments } from "../Service";
import CommentForm from "./CommentForm";
export default class CommentBox extends Component<{
  articleId?: number
}> {
  constructor(props) {
    super(props);

    this.state = {
      comments: [],
      showInputForm: false
    };
    this.toggleForm = this.toggleForm.bind(this);
  }

  toggleForm = () => {
    const { showInputForm } = this.state.showInputForm;
    this.setState({
      showInputForm: !this.state.showInputForm
    });
  };

  toggle() {
    this.setState({
      dropdownOpen: !this.state.dropdownOpen
    });
  }

  componentDidMount() {
    getComments(this.props.articleId)
      .then(response => {
        console.log(response);
        this.setState({ comments: response.data });
      })
      .catch(error => {
        console.log(error);
        this.setState({ errorMsg: "Error retreiving data" });
      });
  }
  render() {
    const { comments } = this.state;
    return (
      <div>
        {this.state.showInputForm && (
          <div className="commentBox">
            <div className="commentList">
              {comments.map(comment => commentCard(comment))}
            </div>
            <CommentForm articleId={this.props.articleId} />
          </div>
        )}
        <Button
          className="fa fa-edit fa-custom"
          onClick={this.toggleForm}
        >
          <a className="fa-custom"> ENDRE SAK</a>
        </Button>
      </div>
    );
  }
}

function commentCard(comments) {
  return (
    <div className="comment p-2 border">
      <h5 className="commentAuthor">{comments.brukernavn}</h5>
      <hr />
      <div>{comments.kommentar}</div>
    </div>
  );
}
