import React, { Component } from "react";
import "../styles/LiveFeed.css";

export default class LiveFeed extends Component {
  constructor(props) {
    super(props);

    this.state = {
      news: [],
      isLoaded: false,
      errorMsg: null
    };
  }

  componentDidMount() {
    axios
      .get("http://localhost:8000/sak")
      .then(response => {
        console.log(response);
        this.setState({ news: response.data });
        this.setState({ isLoaded: true });
      })
      .catch(error => {
        console.log(error);
        this.setState({ errorMsg: "Error retreiving data" });
      });
  }

  render() {
    const { news, isLoaded, errorMsg } = this.state;
    if (errorMsg) {
      return <div>{errorMsg.message}</div>;
    } else if (!isLoaded) {
      return <div>Loading...</div>;
    } else {
      return (
        <div class="marquee">
          <div>SISTE NYTT</div>
          <p>{news.map(news => getLatestNews(news))}</p>
        </div>
      );
    }
  }
}

function getLatestNews(news: Sak) {
  return <a>{news.overskrift} &emsp; &emsp; &emsp; &emsp; </a>;
}
