import React, { Component } from "react";
import News from "./News";
import Sak from "./Sak";
import { getBreakingNews } from "../Service";
import { NavLink } from "react-router-dom";


export default class BreakingNews extends Component {
  constructor(props) {
    super(props);

    this.state = {
      news: [],
      isLoaded: false,
      errorMsg: null,
    };
  }

  changeHandler = e => {
    this.setState({ [e.target.name]: e.target.value });
  };

  toggle() {
    this.setState({
      dropdownOpen: !this.state.dropdownOpen
    });
  }

  componentDidMount() {
    getBreakingNews()
      .then(response => {
        this.setState({ news: response });
        this.setState({ isLoaded: true });
      })
      .catch(error => {
        console.log(error);
        this.setState({ errorMsg: "Error retreiving data" });
      });
  }

  render() {
    const { news, isLoaded, errorMsg } = this.state;
    if (errorMsg) {
      return <div>{errorMsg.message}</div>;
    } else if (!isLoaded) {
      return <div>Loading...</div>;
    } else {
      var newsList = [];
      news.map(news => {
        newsList.push(
          new Sak(
            news.sak_id,
            news.overskrift,
            news.ingress,
            news.innhold,
            news.tidspunkt,
            news.bilde,
            news.kategori_navn,
            news.viktighet,
            news.tommelOpp,
            news.tommelNed
          )
        );
      });

      var headline = newsList[0];
      newsList.shift();
      return (
        <div className="mx-5 px-5 media-custom">
          <div className="row justify-content-center align-items-center mx-5">
            <div className="mx-5 px-5 media-custom">
              <News
                title={headline.overskrift}
                ingress={headline.ingress}
                src={headline.bilde}
                bgColor="danger"
                id={headline.sak_id}
                lastUpdate={headline.tidspunkt}
                upvotes={headline.tommelOpp}
                downvotes={headline.tommelNed}
              />
            </div>
          </div>

          <div className="card-columns mx-5">
            {newsList.map(news => newsCard(news))}
          </div>
        </div>
      );
    }
  }
}
function newsCard(news: Sak) {
  return (
    <News
      title={news.overskrift}
      ingress={news.ingress}
      src={news.bilde}
      id={news.sak_id}
      lastUpdate={news.tidspunkt}
      upvotes={news.tommelOpp}
      downvotes={news.tommelNed}
    ></News>
  );
}

function sortBy(sort) {
  return (
    <DropdownItem>
      <button>{sort}</button>
    </DropdownItem>
  );
}

function translateText(sort) {
  if (sort == "Likes") return "tommelOpp";
  else if (sort == "Dislikes") return "tommelNed";
  else return "overskrift";
}
