import React, { Component } from "react";
import { Button } from "reactstrap";
import "../styles/Article.css";
import { deleteNews, rateNews } from "../Service";

export default class Rating extends Component<{
  articleId?: number,
  upvotes?: number,
  downvotes?: number
}> {
  constructor(props) {
    super(props);

    this.state = {
      tommelOpp: this.props.upvotes,
      tommelNed: this.props.downvotes
    };
  }

  submitHandler = e => {
    rateNews(this.props.articleId, this.state)
      .then(response => {
        console.log(this.state.tommelOpp);
        window.location.reload();
      })
      .catch(error => {
        console.log(error);
      });
  };

  upVote = () => {
    let newCount = this.state.tommelOpp + 1;
    this.setState(
      {
        tommelOpp: newCount
      },
      this.submitHandler
    );
  };

  downVote = () => {
    let newCount = this.state.tommelNed - 1;
    this.setState({
      tommelNed: newCount
    });
    rateNews(this.props.articleId, this.state)
      .then(response => {
        console.log(this.state.tommelNed);
        //window.location.reload();
      })
      .catch(error => {
        console.log(error);
      });
  };

  render() {
    return (
      <div className="text-center my-3">
        <Button
          name="tommelOpp"
          className="fa fa-thumbs-up fa-custom m-2"
          onClick={this.upVote}
        ></Button>
        <Button
          name="tommelNed"
          className="fa fa-thumbs-down fa-custom m-2"
          onClick={this.downVote}
        ></Button>
      </div>
    );
  }
}
