import React, { Component } from "react";
import "../styles/stylesheet.css";
import Navbar from "../components/Navbar.js";
import News from "../components/News.js";
import Card from "../components/Card";
import LiveFeed from "../components/LiveFeed";
import Footer from "../components/Footer.js";
import Sak from "../components/Sak";
import axios from "axios";

const { API_KEY } = process.env
const API_URL = 'http://localhost:8000/sok'

export default class Search extends Component {
  constructor(props) {
    super(props);

    this.state = {
      news: [],
      query: "",
    };
  }

  getInfo = () => {
    axios
      .get(`${API_URL}?api_key=${API_KEY}&prefix=${this.state.query}&limit=5`)
      .then(({ data }) => {
        this.setState({
          results: data.data // MusicGraph returns an object named data,
          // as does axios. So... data.data
        });
      });
  };

  handleInputChange = () => {
    this.setState(
      {
        query: this.search.value
      },
      () => {
        if (this.state.query && this.state.query.length > 1) {
          if (this.state.query.length % 2 === 0) {
            this.getInfo();
          }
        }
      }
    );
  };

  render() {
    const { news, isLoaded, errorMsg } = this.state;
    if (errorMsg) {
      return <div>{errorMsg.message}</div>;
    } else if (!isLoaded) {
      return <div>Loading...</div>;
    } else {
      return (
        <div className="row justify-content-center align-items-center">
          {news.map(news => newsCard(news))}
        </div>
      );
    }
  }
}
function newsCard(news: Sak) {
  return (
    <Card cardSize="25">
      <News
        title={news.overskrift}
        src={news.bilde}
        alt="Alt tekst"
        href="#"
        colSize="4"
      ></News>
    </Card>
  );
}
