import React, { Component } from "react";
import Navbar from "../components/Navbar.js";
import Footer from "../components/Footer.js";
import axios from "axios";
import {
  Container,
  ButtonDropdown,
  DropdownMenu,
  DropdownItem,
  DropdownToggle
} from "reactstrap";

const categoryList = [{ id: 1, name: "Kultur" }, { id: 2, name: "Sport" }];

// mimicing API request
const getGender = () =>
  new Promise(resolve =>
    setTimeout(() => {
      resolve(categoryList);
    }, 1000)
  );

export default class Register extends Component {
  constructor(props) {
    super(props);
    this.toggle = this.toggle.bind(this);
    this.select = this.select.bind(this);

    this.state = {
      overskrift: "",
      innhold: "",
      bilde: "",
      kategori_id: "",
      viktighet: "",

      dropdownOpen: false,
      value: "Kategori",

      gender: "",
      categoryList: []
    };
  }

  toggle() {
    this.setState({
      dropdownOpen: !this.state.dropdownOpen
    });
  }

  select(event) {
    this.setState({
      dropdownOpen: !this.state.dropdownOpen,
      value: event.target.innerText
    });
  }

  componentDidMount() {
    getGender().then(data =>
        this.setState({ genderCat: data, gender: data[ 0 ].name }));
  }

  createOptions = () =>
    this.state.categoryList.length
      ? this.state.categoryList.map(data => (
          <DropdownItem key={data.id} value={data.name} class="form-control"
          type="number"
          min="1"
          max="2"
          name="kategori_id"
          value={1}
          onChange={this.changeHandler}
          onClick={this.select}>
            {data.name}
          </DropdownItem>
        ))
      : "";

  changeHandler = e => {
    this.setState({ [e.target.name]: e.target.value });
  };

  submitHandler = e => {
    e.preventDefault();
    console.log(this.state);
    axios
      .post("http://localhost:8000/registrerSak", this.state)
      .then(response => {
        console.log(response);
      })
      .catch(error => {
        console.log(error);
      });
    showAlert();
    window.location.hash = "/sak";
  };

  render() {
    const { overskrift, innhold, bilde, kategori_id, viktighet } = this.state;
    return (
      <div>
        <Navbar />
        <div class="jumbotron jumbotron-fluid">
          <div class="container">
            <h1 class="display-4">Registrer en sak!</h1>
            <p class="lead">
              Her kan du laste opp en nyhetssak til v√•r nettside.
            </p>
          </div>
        </div>

        <form onSubmit={this.submitHandler}>
          <div class="form-group mx-5">
            <label>Tittel</label>
            <input
              type="text"
              name="overskrift"
              value={overskrift}
              onChange={this.changeHandler}
              class="form-control"
              placeholder="Skriv inn tittelen til din sak"
            ></input>
          </div>
          <div class="form-group mx-5">
            <label>Beskrivelse</label>
            <textarea
              type="text"
              name="innhold"
              class="form-control"
              value={innhold}
              onChange={this.changeHandler}
              placeholder="Skriv inn det innholdet du √∏nsker vist i saken"
              rows="5"
            ></textarea>
          </div>
          <div class="form-group mx-5">
            <label>Bilde-URL</label>
            <input
              type="text"
              name="bilde"
              value={bilde}
              onChange={this.changeHandler}
              class="form-control"
              placeholder="Skriv inn bilde-URL til din sak"
            ></input>
          </div>
          <div class="form-group mx-5">
            <ButtonDropdown
              isOpen={this.state.dropdownOpen}
              toggle={this.toggle}
            >
              <DropdownToggle>{this.state.value}</DropdownToggle>
              <DropdownMenu>
                <DropdownItem
                  class="form-control"
                  type="number"
                  min="1"
                  max="2"
                  name="kategori_id"
                  value={1}
                  onChange={this.changeHandler}
                  onClick={this.select}
                >
                  Kultur
                </DropdownItem>
                <DropdownItem>Sport</DropdownItem>
              </DropdownMenu>
            </ButtonDropdown>
          </div>
          <div class="form-group mx-5">
            <label>Viktighet</label>
            <input
              type="number"
              min="1"
              max="2"
              name="viktighet"
              value={viktighet}
              onChange={this.changeHandler}
              class="form-control"
              placeholder="Skriv inn viktighet av din sak"
            ></input>
          </div>

          <button type="submit" class="btn btn-primary mx-5">
            Submit
          </button>
        </form>
        <Footer />
      </div>
    );
  }
}
function showAlert() {
  alert("Saken er registrert");
}

function checkCategory(category: string) {
  if (category == "Kultur") return 1;
}
