import React, { Component } from "react";
import { getComments, addComment } from "../Service";
export default class CommentForm extends Component {
  constructor(props) {
    super(props);

    this.state = {
      brukernavn: "",
      kommentar: ""
    };
  }

  required() {
    if (this.state.brukernavn === "" || this.state.kommentar === "") {
      return false;
    }
    return true;
  }

  changeHandler = e => {
    this.setState({ [e.target.name]: e.target.value });
  };

  submitHandler = e => {
    e.preventDefault();
    console.log(this.state);
    if (window.confirm("Er du sikker?")) {
      if (this.required()) {
        addComment(this.state)
          .then(response => {
            console.log(response);
            alert("Kommentaren er registrert");
            window.location.hash = "";
          })
          .catch(error => {
            console.log(error);
          });
      } else {
        alert("Vennligst fyll inn alle feltene");
      }
    }
  };

  render() {
    const { brukernavn, kommentar } = this.state;
    return (
      <form onSubmit={this.submitHandler}>
        <input
          type="text"
          placeholder="Brukernavn"
          value={brukernavn}
          onChange={this.changeHandler}
        />
        <input
          type="text"
          placeholder="Kommentar"
          value={kommentar}
          onChange={this.changeHandler}
        />
        <button
          type="submit"
          class="btn btn-danger  btn-sm fa fa-send fa-custom"
        >
          {" "}
          SEND INN
        </button>
      </form>
    );
  }
}
