import React, { Component } from "react";
import { Link } from "react-router-dom";
import * as $ from "jquery";

class BN {
  overskrift: string;
  innhold: string;
  tidspunkt: timestamp;
  bilde: string;
  kategori_id: number;
  viktighet: number;

  constructor(
    overskrift: string,
    innhold: string,
    tidspunkt: timestamp,
    bilde: string,
    kategori_navn: string,
    viktighet: number
  ) {
    this.overskrift = overskrift;
    this.innhold = innhold;
    this.tidspunkt = tidspunkt;
    this.bilde = bilde;
    this.kategori_navn = kategori_navn;
    this.viktighet = viktighet;
  }
}

export default class BigNews extends Component<{
  title?: string,
  src?: string,
  alt?: string,
  href?: string
}> {
  render() {
    var newsList = [];
    $.ajax({
      url: "/sak",
      type: "GET",
      dataType: "json",
      success: function(data) {
        data.map(news => {
          newsList.push(
            new BN(
              news.overskrift,
              news.innhold,
              news.tidspunkt,
              news.bilde,
              news.kategori_id,
              news.viktighet
            )
          );
        });
      },
      async: false
    });
    return (
      <div className="col-sm-">
        <Link className="text-body" exact to={this.props.href}>
          <div
            className="card mx-auto bg-danger border-0"
            style={{ width: "auto" }}
          >
            <div>
              <img
                src={this.props.src}
                className="img-fluid"
                alt={this.props.alt}
              />
            </div>
            <div className="card-body">
              <h1 className="text-center">{this.props.title}</h1>
            </div>
          </div>
        </Link>
        <div class="row">
          {newsList
            .filter(news => news.viktighet == 1)
            .map(viktighet => newsCard(viktighet))}
        </div>
      </div>
    );
  }
}

function newsCard(BigNews: BN) {
  return (
    <div className="col-sm-4 my-3">
      <div className="card">
        <div className="card-block">
          <h4 className="card-title custom-title-movies">
            <a>{BigNews.overskrift}</a>
          </h4>
        </div>
      </div>
    </div>
  );
}
