import React, { Component } from "react";
import { Link } from "react-router-dom";

export default class BigNews extends Component<{
  title?: string,
  src?: string,
  alt?: string,
  href?: string
}> {
  render() {
    var bigNewsList = [];
    app.get("/sak", (req, res) => {
      console.log("Fikk request fra klient");
      pool.getConnection((err, connection) => {
        console.log("Connected to database");
        if (err) {
          console.log("Feil ved kobling til databasen");
          res.json({ error: "feil ved ved oppkobling" });
        } else {
          connection.query(
            "select overskrift, innhold, tidspunkt, bilde, kategori_navn from sak join kategori using(kategori_id)",

            (err, rows) => {
              connection.release();
              if (err) {
                console.log(err);
                res.json({ error: "error querying" });
              } else {
                console.log(rows);
                res.json(rows);
              }
            }
          );
        }
      });
    });
    return (
      <div className="col-sm-" style={{ margin: "1rem" }}>
        <Link class="text-body" exact to={this.props.href}>
          <div
            className="card mx-auto bg-danger border-0"
            style={{ width: "auto" }}
          >
            <div>
              <img
                src={this.props.src}
                class="img-fluid"
                alt={this.props.alt}
              />
            </div>
            <div className="card-body">
              <h1 className="text-center">{this.props.title}</h1>
            </div>
          </div>
        </Link>
      </div>
    );
  }
}
