import React, { Component } from "react";
import { NavLink } from "react-router-dom";
import "../styles/stylesheet.css";
import Navbar from "../components/Navbar.js";
import News from "../components/News.js";
import MedNews from "../components/MedNews.js";
import SmallNews from "../components/SmallNews.js";
import Card from "../components/Card";
import Row from "../components/Row";
import Column from "../components/Column";
import Sak from "../components/Sak";
import axios from "axios";
import * as $ from "jquery";

export default class BigNews extends Component {
  constructor(props) {
    super(props);

    this.state = {
      news: []
    };
  }

  componentDidMount() {
    axios
      .get("/sak")
      .then(response => {
        console.log(response);
        this.setState({ news: response.data });
      })
      .catch(error => {
        console.log(error);
      });
  }

  render() {
    const { news } = this.state;
    var newsList = [];
    news.map(e => {
      newsList.push(
        new Sak(e.overskrift, e.innhold, e.bilde, e.kategori_id, e.viktighet)
      );
    });
    var BigNewsList = newsList.filter(news => news.viktighet === 1);
    var topNews = BigNewsList[0];
    BigNewsList.shift();

    return (
      <div className="row justify-content-center align-items-center">
        <Card>
          <Row>
            <News
              title={topNews.overskrift}
              //src="../resources/trump.jpg"
              src={topNews.bilde}
              alt="Trump og Northug jr."
              href="/article"
            />
          </Row>
        </Card>
        <Card>
          <div className="row justify-content-center align-items-center">
            {BigNewsList.map(e => newsCard(e))}
          </div>
        </Card>
      </div>
    );
  }
}
function newsCard(news: Sak) {
  return (
    <News
      title={news.overskrift}
      src={news.bilde}
      alt="Alt tekst"
      href="#"
      colSize="4"
    ></News>
  );
}
