import React, { Component } from "react";
import { Button } from "reactstrap";
import "../styles/Article.css";
import { deleteNews, rateNews } from "../Service";

export default class Rating extends Component<{
  articleId?: number,
  upvotes?: number,
  downvotes?: number
}> {
  constructor(props) {
    super(props);

    this.state = {
      sak_id: this.props.articleId,
      tommelOpp: this.props.upvotes,
      tommelNed: this.props.downvotes
    };
  }

  submitHandler = e => {
    rateNews(this.state.sak_id, this.state)
      .then(response => {
        console.log(this.state.sak_id);
        console.log(this.state.tommelOpp);
        console.log(this.state.tommelNed);
      })
      .catch(error => {
        console.log(error);
      });
  };

  upVote = () => {
    let newCount = this.state.tommelOpp + 1;
    this.setState(
      {
        tommelOpp: newCount
      },
      this.submitHandler
    );
  };

  downVote = () => {
    let newCount = this.state.tommelNed - 1;
    this.setState(
      {
        tommelNed: newCount
      },
      this.submitHandler
    );
