import React, { Component } from "react";
import { Button } from "reactstrap";
import "../styles/Article.css";
import { deleteNews } from "../Service";

export default class Rating extends Component<{
  upvotes?: number,
  downvotes?: number
}> {
  constructor(props) {
    super(props);

    this.state = {
      tommelOpp: this.props.upvotes,
      tommelNed: this.props.downvotes
    };
  }

  changeHandler = e => {
    this.setState({ [e.target.name]: e.target.value });
  };

  upVote = () => {
    let newCount = this.state.tommelOpp + 1;
    this.setState({
      count: newCount
    });
  };

  downVote = () => {
    let newCount = this.state.count + 1;
    this.setState({
      count: newCount
    });
  };


  render() {
    return (
      <div className="text-center my-3">
        <Button
          name="tommelOpp"
          className="fa fa-thumbs-up fa-custom m-2"
          onClick={this.changeHandler}
        ></Button>
        <Button
          name="tommelNed"
          className="fa fa-thumbs-down fa-custom m-2"
          onClick={this.changeHandler}
        ></Button>
      </div>
    );
  }
}
