import React, { Component } from "react";
import "../styles/stylesheet.css";
import News from "../components/News.js";
import MedNews from "../components/MedNews.js";
import SmallNews from "../components/SmallNews.js";
import Card from "../components/Card";
import Row from "../components/Row";
import Column from "../components/Column";
import * as $ from "jquery";

export default class BigNews extends Component {
  render() {
    var newsList = [];
    $.ajax({
      url: "/sak",
      type: "GET",
      dataType: "json",
      success: function(data) {
        data.map(news => {
          newsList.push(
            new Sak(
              news.overskrift,
              news.innhold,
              news.tidspunkt,
              news.bilde,
              news.kategori_id,
              news.viktighet
            )
          );
        });
      },
      async: false
    });
    var BigNewsList = newsList.filter(news => news.viktighet == 1);
    return (
      <Card>
        <Row>
          <Row>
            <News
              title="Trump adopterer Greta Thunberg"
              src="../resources/trump.jpg"
              alt="Trump og Northug jr."
              href="/article"
            />
          </Row>
        </Row>

        <Row>
          <Row>
            <MedNews
              title=" Trump Dump til angrep på Northug"
              src="../resources/trump.jpg"
              alt="Trump og Northug jr."
              href="#"
            />
            <SmallNews
              title=" Trump Dump til angrep på Northug"
              src="../resources/trump.jpg"
              alt="Trump og Northug jr."
              href="#"
            />
          </Row>
        </Row>
      </Card>
    );
  }
}
function newsCard(BigNews: Sak) {
  return (
    <div className="col-sm-4 my-3">
      <div className="card">
        <div className="card-block">
          <h4 className="card-title custom-title-movies">
            <a>{BigNews.overskrift}</a>
          </h4>
        </div>
      </div>
    </div>
  );
}
