import React, { Component } from "react";
import { NavLink } from "react-router-dom";
import "../styles/stylesheet.css";
import Navbar from "../components/Navbar.js";
import News from "../components/News.js";
import MedNews from "../components/MedNews.js";
import SmallNews from "../components/SmallNews.js";
import Card from "../components/Card";
import Row from "../components/Row";
import Column from "../components/Column";
import Sak from "../components/Sak";
import axios from "axios";
import * as $ from "jquery";

export default class BigNews extends Component {
  constructor(props) {
    super(props);

    this.state = {
      news: [],
      errorMsg: ""
    };
  }

  render() {
    axios
      .get("/sak")
      .then(response => {
        console.log(response);
        this.setState({ news: response.data });
      })
      .catch(error => {
        console.log(error);
        this.setState({ errorMsg: "Error retreiving data" });
      });
    const { news, errorMsg } = this.state;
    var newsList = [];
    news.map(news => {
      newsList.push(
        new Sak(
          news.overskrift,
          news.innhold,
          news.tidspunkt,
          news.bilde,
          news.kategori_navn,
          news.viktighet
        )
      );
    });
    return (
      <div className="row justify-content-center align-items-center">
        <Card>
          <Row>
            <News
              title={newsList[0].overskrift}
              //src="../resources/trump.jpg"
              src={newsList[0]}
              alt="Trump og Northug jr."
              href="/article"
            />
            {/* {news.length
              ? news.map(news => (
                  <div key={news.overskrift}>{news.overskrift}</div>
                ))
              : null}
            {errorMsg ? <div>{errorMsg}</div> : null} */}
          </Row>
        </Card>
        <Card>
          <div className="row justify-content-center align-items-center"></div>
        </Card>
      </div>
    );
  }
}
function newsCard(news: Sak) {
  return (
    <News
      title={news.overskrift}
      src={news.bilde}
      alt="Alt tekst"
      href="#"
      colSize="4"
    ></News>
  );
}
